var one_day=1000*60*60*24;var one_month=1000*60*60*24*30;var one_year=1000*60*60*24*30*12;function displayage(h,b,d,e,c,j){today=new Date();var i=new Date(h,b-1,d);var f=e;var a=c;var g=j;finalunit=(f=="days")?one_day:(f=="months")?one_month:one_year;a=(a<=0)?1:a*10;if(e!="years"){if(g=="rounddown"){document.write(Math.floor((today.getTime()-i.getTime())/(finalunit)*a)/a+" "+f)}else{document.write(Math.ceil((today.getTime()-i.getTime())/(finalunit)*a)/a+" "+f)}}else{yearspast=today.getFullYear()-h-1;tail=(today.getMonth()>b-1||today.getMonth()==b-1&&today.getDate()>=d)?1:0;i.setFullYear(today.getFullYear());pastdate2=new Date(today.getFullYear()-1,b-1,d);tail=(tail==1)?tail+Math.floor((today.getTime()-i.getTime())/(finalunit)*a)/a:Math.floor((today.getTime()-pastdate2.getTime())/(finalunit)*a)/a;document.write(yearspast+tail)}};