function googlereader(){var a=new google.feeds.Feed("http://www.google.com/reader/public/atom/user%2F02227762350607711900%2Fstate%2Fcom.google%2Fbroadcast");a.setNumEntries(5);a.load(function(b){var d=document.getElementById("googleReaderContent");if(!b.error){for(var e=0;e<b.feed.entries.length;e++){var g=b.feed.entries[e];var f=document.createElement("a");f.href=g.link;f.className="contentlink";f.appendChild(document.createTextNode(unescape(g.title)));f.appendChild(document.createElement("br"));f.appendChild(document.createElement("br"));d.appendChild(f)}}else{var c=document.createElement("div");c.id="googlereaderError";c.appendChild(document.createTextNode("Failed to load articles, refresh"));d.appendChild(c)}})};