function twitter(){var a=new google.feeds.Feed("http://twitter.com/statuses/user_timeline/iancrowther.rss");a.setNumEntries(5);a.load(function(b){var d=document.getElementById("twitterContent");if(!b.error){for(var e=0;e<b.feed.entries.length;e++){var g=b.feed.entries[e];var f=document.createElement("a");f.href=g.link;f.className="contentlink";f.appendChild(document.createTextNode(unescape(g.title.substr(13))));f.appendChild(document.createElement("br"));f.appendChild(document.createElement("br"));d.appendChild(f)}}else{var c=document.createElement("div");c.id="twitterError";c.appendChild(document.createTextNode("Failed to load tweets, refresh"));d.appendChild(c)}})};